--
-- 表的结构 `blog_plugins`
--

CREATE TABLE `blog_plugins` (
  `id` int(11) UNSIGNED NOT NULL COMMENT '自增id',
  `name` varchar(50) NOT NULL COMMENT '插件名，英文',
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '插件名称',
  `description` text COMMENT '插件描述',
  `type` tinyint(2) DEFAULT '0' COMMENT '插件类型, 1:网站；8;微信',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态；1开启；',
  `config` text COMMENT '插件配置',
  `hooks` varchar(255) DEFAULT NULL COMMENT '实现的钩子;以“，”分隔',
  `has_admin` tinyint(2) DEFAULT '0' COMMENT '插件是否有后台管理界面',
  `author` varchar(50) DEFAULT '' COMMENT '插件作者',
  `version` varchar(20) DEFAULT '' COMMENT '插件版本号',
  `createtime` int(11) UNSIGNED NOT NULL DEFAULT '0' COMMENT '插件安装时间',
  `listorder` smallint(6) NOT NULL DEFAULT '0' COMMENT '排序'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='插件表';

-- --------------------------------------------------------

--
-- 表的结构 `blog_posts`
--

CREATE TABLE `blog_posts` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED DEFAULT '0' COMMENT '发表者id',
  `post_keywords` varchar(150) NOT NULL COMMENT 'seo keywords',
  `post_source` varchar(150) DEFAULT NULL COMMENT '转载文章的来源',
  `post_date` datetime DEFAULT '2000-01-01 00:00:00' COMMENT 'post发布日期',
  `post_content` longtext COMMENT 'post内容',
  `post_title` text COMMENT 'post标题',
  `post_excerpt` text COMMENT 'post摘要',
  `post_status` int(2) DEFAULT '1' COMMENT 'post状态，1已审核，0未审核,3删除',
  `comment_status` int(2) DEFAULT '1' COMMENT '评论状态，1允许，0不允许',
  `post_modified` datetime DEFAULT '2000-01-01 00:00:00' COMMENT 'post更新时间，可在前台修改，显示给用户',
  `post_content_filtered` longtext,
  `post_parent` bigint(20) UNSIGNED DEFAULT '0' COMMENT 'post的父级post id,表示post层级关系',
  `post_term` varchar(340) NOT NULL,
  `term_name` varchar(640) NOT NULL,
  `post_type` int(2) DEFAULT '1' COMMENT 'post类型，1文章,2页面',
  `post_mime_type` varchar(100) DEFAULT '',
  `comment_count` bigint(20) DEFAULT '0',
  `smeta` text COMMENT 'post的扩展字段，保存相关扩展属性，如缩略图；格式为json',
  `post_hits` int(11) DEFAULT '0' COMMENT 'post点击数，查看数',
  `post_like` int(11) DEFAULT '0' COMMENT 'post赞数',
  `istop` tinyint(1) NOT NULL DEFAULT '0' COMMENT '置顶 1置顶； 0不置顶',
  `recommended` tinyint(1) NOT NULL DEFAULT '0' COMMENT '推荐 1推荐 0不推荐'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Portal文章表';

--
-- 转存表中的数据 `blog_posts`
--

INSERT INTO `blog_posts` (`id`, `post_author`, `post_keywords`, `post_source`, `post_date`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `post_modified`, `post_content_filtered`, `post_parent`, `post_term`, `term_name`, `post_type`, `post_mime_type`, `comment_count`, `smeta`, `post_hits`, `post_like`, `istop`, `recommended`) VALUES
(1, 1, '', NULL, '2017-03-30 13:39:58', '<p>fwefwe</p>', '我单位', '', 3, 1, '2017-03-30 13:39:51', NULL, 0, '', '', 2, '', 0, '{"template":"page","thumb":""}', 7, 0, 0, 0),
(2, 1, '', '慕课网http://www.imooc.com/article/17248', '2017-03-30 13:51:55', '<p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">为增强小程序能力，扩大小程序使用场景，现小程序新增以下能力：</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">1、个人开发者可申请小程序</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">小程序开放个人开发者申请注册，个人用户可访问微信公众平台，扫码验证个人身份后即可完成小程序帐号申请并进行代码开发。</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">请访问：<a href="https://mp.weixin.qq.com/cgi-bin/registermidpage?action=index&lang=zh_CN" target="_blank" rel="nofollow" style="outline: 0px;color: rgb(20, 25, 30)">微信公众平台(mp.weixin.qq.com)</a></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img 1="" title="重磅！小程序新增六大功能，面向个人开发者开放_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d2cec2e8.jpg" alt="图片描述" style="border: 0px"/></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">2、公众号自定义菜单点击可打开相关小程序</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">公众号可将已关联的小程序页面放置到自定义菜单中，用户点击后可打开该小程序页面。公众号运营者可在公众平台进行设置，也可以通过自定义菜单接口进行设置。</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">详见开发文档：<a href="http://mp.weixin.qq.com/wiki" target="_blank" rel="nofollow" style="outline: 0px;color: rgb(20, 25, 30)">微信公众平台文档(mp.weixin.qq.com/wiki)-自定义菜单</a></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img 2="" title="重磅！小程序新增六大功能，面向个人开发者开放_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d2d347d8.jpg" alt="图片描述" style="border: 0px"/></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">3、公众号模版消息可打开相关小程序</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">公众号已关联的小程序页面可以配置到公众号的模版消息中，用户点击公众号下发的模版消息，可以打开对应的小程序页面。</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">详见开发文档：<a href="https://mp.weixin.qq.com/wiki" target="_blank" rel="nofollow" style="outline: 0px;color: rgb(20, 25, 30)">微信公众平台文档(mp.weixin.qq.com/wiki)-模版消息</a></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">4、公众号关联小程序时，可选择给粉丝下发通知</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">公众号关联小程序时，可选择给粉丝下发通知消息，粉丝点击该通知消息可以打开小程序。该消息不占用原有群发条数。</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img 3="" title="重磅！小程序新增六大功能，面向个人开发者开放_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d2d72bf0.jpg" alt="图片描述" style="border: 0px"/></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">5、移动App可分享小程序页面</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">开发者可以把小程序绑定到微信开放平台。绑定后，同一微信开放平台帐号下的App可分享已绑定的小程序页面到微信内的会话或群聊。</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">详见开发文档：<a href="https://open.weixin.qq.com/cgi-bin/showdocument?action=dir_list&t=resource/res_list&verify=1&id=open1419317332&token=&lang=zh_CN" target="_blank" rel="nofollow" style="outline: 0px;color: rgb(20, 25, 30)">微信开放平台(open.weixin.qq.com)-资源中心-移动应用-分享与收藏功能</a></p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">6、扫描普通链接二维码可打开小程序</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">商户如果在线下已铺设了普通链接二维码，可在公众平台的小程序管理后台进行配置，用户扫描该原有线下普通链接二维码可直接打开小程序。</p><p><br style="color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"/><span style="color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;background-color: rgb(255, 255, 255)">作者： 小慕偶&nbsp;</span><br style="color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"/><span style="color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;background-color: rgb(255, 255, 255)">链接：http://www.imooc.com/article/17248</span><br style="color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"/><span style="color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;background-color: rgb(255, 255, 255)">来源：慕课网</span></p><p><br/></p>', '重磅！小程序新增六大功能，面向个人开发者开放', '为增强小程序能力，扩大小程序使用场景，现小程序新增以下能力：\r\n1、个人开发者可申请小程序\r\n小程序开放个人开发者申请注册，个人用户可访问微信公众平台，扫码验证个人身份后即可完成小程序帐号申请并进行代码开发。\r\n请访问：微信公众平台(mp.weixin.qq.com)', 1, 1, '2017-04-01 13:22:53', NULL, 0, '["11"]', '', 1, '', 0, '{"thumb":"","template":""}', 5, 0, 0, 0),
(3, 1, '', '慕课网http://www.imooc.com/article/17097', '2017-03-30 13:53:50', '<p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">大家好，本文给各位做一下vue.js一个最基本的概念介绍。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">vue.js 指令</span></p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">&lt;div&nbsp;id=&quot;app&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&lt;div&nbsp;v-text=&quot;message&quot;&gt;&lt;/div&gt;&lt;/div&gt;</pre><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;app&nbsp;=&nbsp;new&nbsp;Vue({\r\n&nbsp;&nbsp;el:&nbsp;&#39;#app&#39;,\r\n&nbsp;&nbsp;data:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;message:&nbsp;&#39;Hello&nbsp;Vue!&#39;\r\n&nbsp;&nbsp;}})</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这个例子我们会得到，v-text所在的div元素的内部插入了&#39;Hello Vue！&#39;这段字符串，那么我们为什么会产生这样一个结果呢，我们来分析一下：</p><hr style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img 1="" title="vue.js功能介绍-指令，组件_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d90ad570.jpg" alt="图片描述" style="border: 0px;"/><br/>这一步的意思是，实例化的这个Vue接管了#app元素，这里面的所有内容归我管，也就是说可以接受vue的功能指令。</p><hr style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img 2="" title="vue.js功能介绍-指令，组件_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d90e9a48.jpg" alt="图片描述" style="border: 0px;"/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">第二步是在#app里， v-text指令被识别出来，它接受的参数是&#39;message&#39;，vue获取到这个参数，并在自己实例化的data里查找叫message的属性，找到message的内容原来是&#39;hello vue&#39;, 于是把&#39;hello vue&#39; 渲染到页面。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">类似的指令，还有v-if，条件判断，v-for，列表渲染，v-on进行元素的事件绑定。等等。vue.js与页面也就是template进行交互，主要是通过这些内置指令来完成的。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">vue.js 组件</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">开发前端页面，模块化有很多好处，比如公用的sidebar，比如公用的日期选择器。<br/>模块化还有一个好处，就是简化开发逻辑，让项目具有扩展性。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">vue.js的模块化主要体现在组件上，以组件为单位进行模块化。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">我们可以在实例化vue对象之前，通过Vue.component方法来注册全局的组件，比如：</p><blockquote style="margin: 0px; padding: 0px; quotes: none; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px;">全局组件</p></blockquote><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">//&nbsp;告诉Vue，我需要一个组件叫做todo-item，它的配置如下，就是包含props和template那个对象Vue.component(&#39;todo-item&#39;,&nbsp;{\r\n&nbsp;&nbsp;props:&nbsp;[&#39;todo&#39;],\r\n&nbsp;&nbsp;template:&nbsp;&#39;&lt;li&gt;{{&nbsp;todo.text&nbsp;}}&lt;/li&gt;&#39;})&nbsp;//&nbsp;实例化一个vue对象，挂载在#app-7这个元素下，并且设定它的属性，groceryList，是一个数组var&nbsp;app7&nbsp;=&nbsp;new&nbsp;Vue({\r\n&nbsp;&nbsp;el:&nbsp;&#39;#app-7&#39;,\r\n&nbsp;&nbsp;data:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;groceryList:&nbsp;[\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;text:&nbsp;&#39;Vegetables&#39;&nbsp;},\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;text:&nbsp;&#39;Cheese&#39;&nbsp;},\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;text:&nbsp;&#39;Whatever&nbsp;else&nbsp;humans&nbsp;are&nbsp;supposed&nbsp;to&nbsp;eat&#39;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;]\r\n&nbsp;&nbsp;}})</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">模板：</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">&lt;div&nbsp;id=&quot;app-7&quot;&gt;\r\n&nbsp;&nbsp;&lt;ol&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--因为注册了全局的todo-item组件，所以在挂载范围内我们可以直接使用todo-item标签，来引用这个组件，--&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--组件内部会被&nbsp;todo-item配置表里的template替换，用来渲染。属性todo也是配置对象里规定的，可以接受的参数。--&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;todo-item&nbsp;v-for=&quot;item&nbsp;in&nbsp;groceryList&quot;&nbsp;v-bind:todo=&quot;item&quot;&gt;&lt;/todo-item&gt;\r\n&nbsp;&nbsp;&lt;/ol&gt;&lt;/div&gt;</pre><blockquote style="margin: 0px; padding: 0px; quotes: none; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px;">子组件</p></blockquote><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">实际项目开发中，我们更多的使用的是子组件。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">我们刚刚说的每个组件都有一个配置表，就是一个js对象，比如：</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">{\r\n&nbsp;&nbsp;props:&nbsp;[&#39;todo&#39;],\r\n&nbsp;&nbsp;template:&nbsp;&#39;&lt;li&gt;{{&nbsp;todo.text&nbsp;}}&lt;/li&gt;&#39;}</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">我们把这份配置引入到实例化对象里，就可以成为这个vue对象的子组件，比如：</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">new&nbsp;Vue({\r\n&nbsp;&nbsp;&nbsp;&nbsp;components:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;component-a&#39;&nbsp;:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;props:&nbsp;[&#39;todo&#39;],\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;template:&nbsp;&#39;&lt;li&gt;{{&nbsp;todo.text&nbsp;}}&lt;/li&gt;&#39;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}})</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这样的话，我们和之前全局组件一样，也可以在实例化对象挂载的范围内，直接使用&lt;component-a&gt;标签来引用组件。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">那么接下来，我们考虑一下，子组件里也可以有自己的子组件，比如：</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">new&nbsp;Vue({\r\n&nbsp;&nbsp;&nbsp;&nbsp;components:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;component-a&#39;&nbsp;:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;props:&nbsp;[&#39;todo&#39;],\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;template:&nbsp;&#39;&lt;li&gt;{{&nbsp;todo.text&nbsp;}}&lt;/li&gt;&#39;，\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;components:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;component-b&#39;&nbsp;:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;template:&nbsp;&#39;i&nbsp;am&nbsp;component&nbsp;b&#39;，\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;components:&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;...\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}})</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">那么这样下去，我们可以有无数个子组件，形成一个组件树：</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img 3="" title="vue.js功能介绍-指令，组件_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d912cec0.jpg" alt="图片描述" style="border: 0px;"/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">所以，一个vue.js架构的网站，我们可以看成是一个个组件的开发，最终存在一个根节点，就是项目里唯一实例化的Vue对象，而这一个个组件，其实就是一份份配置对象，以及它们对应的template 模板（html片段）。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">vue.js课程</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><a href="http://coding.imooc.com/class/91.html" target="_blank" rel="nofollow" style="outline: 0px; text-decoration: none; color: rgb(20, 25, 30);">带你入门Vue.js 2.0 开发</a></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img 4="" title="vue.js功能介绍-指令，组件_" src="/BLOG_01/data/upload/ueditor/20170330/58dc9d916e1b8.jpg" alt="图片描述" style="border: 0px;"/><br/>由我主讲的vue.js课程正式上线了，课程会从类似上面的基本概念入手，带领大家理解+学习 vue.js。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">学习本课程需要大家动脑自己思考，触类旁通，相信带给大家超过vue.js本身的知识和思考。</p><p><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">作者： fishenal&nbsp;</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">链接：http://www.imooc.com/article/17097</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">来源：慕课网</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">本文原创发布于慕课网 ，转载请注明出处，谢谢合作！</span></p><p><br/></p>', 'vue.js功能介绍 - 指令，组件', '开发前端页面，模块化有很多好处，比如公用的sidebar，比如公用的日期选择器。\r\n模块化还有一个好处，就是简化开发逻辑，让项目具有扩展性。\r\nvue.js的模块化主要体现在组件上，以组件为单位进行模块化。\r\n我们可以在实例化vue对象之前，通过Vue.component方法来注册全局的组件，比如：\r\n全局组件', 1, 1, '2017-03-30 14:22:24', NULL, 0, '', '', 1, '', 0, '{"thumb":"","template":""}', 2, 0, 0, 0),
(4, 1, '', '', '2017-03-30 13:55:27', '<h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">cookie和session的机制是什么？有什么区别？</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">会话（Session）跟踪是Web程序中常用的技术，用来跟踪用户的整个会话，它可以弥补http协议无状态的不足。常用的会话跟踪技术是cookie与session。cookie通过在客户端记录信息确定用户身份，session通过在服务器端记录信息确定用户身份。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">cookie有什么作用？</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">cookie可以解决http的无状态的问题，与服务器进行交互，作为http规范存在。它具有极高的简便性、可扩展性和可用性，也可以通过加密和SSL技术来提高其安全性。因此推荐使用cookie作为标识而不是身份验证的工具。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">cookie的缺点</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1） 大小和数目受限制。浏览器对一个域cookie的条目数有上限要求，且每个cookie的大小不得超过4kb。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）存在安全性问题，易被人拦截。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）需要指定域，不可以跨域</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（4）浪费带宽，因为我每次请求一个新的页面，cookie都会被自动发送过去。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（5）有的移动端浏览器不支持cookie或浏览器禁用cookie</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（6）有些状态不可能保存在客户端。例如，为了防止重复提交表单，我们需要在服务器端保存一个计数器。如果我们把这个计数器保存在客户端，那么它起不到任何作用。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">如何删除cookie</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在服务器端，以java为例，可以将同名cookie的maxAge属性置0；在客户端，可以将expires属性设置为过去的一个时间。即：<br/>document.cookie = ‘name=’+cookie_name+’;expires=’+ passed_date</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">什么是重排和重绘？什么时候会发生重绘？如何避免重绘？</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">重绘是指的屏幕的一部分要重新绘制，比如元素的背景色发生了改变，但几何尺寸未发生改变。重排意味着元素的几何尺寸发生了改变，需要重新计算生成渲染树。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">下面几种情况会发生重排：</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1）resize窗口时</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）修改网页默认字体时</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）修改CSS样式改变尺寸时</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（4）JS操作dom树</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（5）一些影响布局的动画</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（6）使用JS脚本获得一些布局属性时，比如offsetTop</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">相应的，可以采取以下方法来减少重排:</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1）不要一条条的修改DOM样式，改变className来“统一修改”（注：现代浏览器会进行增量异步reflow，即“攒够”一些修改后再统一修改）</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）把dom离线修改，比如将其至于内存或设置display:none。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）为html动画原件使用position:absolute布局或fixed布局，这样不会引起重排和重绘。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（4）不要使用table布局，因为稍加改动就会导致table的重新布局。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（5）将offsetTop等属性缓存成局部变量。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">浏览器是如何渲染页面的？</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">首先，浏览器会解析html\\xhtml\\svg，生成一个dom树，解析css文件生成css规则树，解析javascript以获得其对html和css的影响。解析完成后，浏览器会根据dom树和css规则树来生成渲染树。渲染树不包括像header元素这样页面中不会出现的元素。之后，会进行layout，定义元素的坐标。最后通过调用系统Native GUI的API绘制。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">网页中如何优化图像</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">网页中优化图片的方式有：</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1）去掉没有意义的修饰</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）使用CSS和矢量图代替位图</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）惰性加载图片，使得图片可见后再进行加载</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（4）使用CSS Sprites，将多张小图拼成一张大图</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">png和jpg格式的区别：</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">png属于无损压缩，而jpg则是有损压缩，这意味着一张图片多次使用jpg格式压缩的话会逐渐失真。png格式的图片存储空间明显大于jpg格式的图片</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">常见的Web攻击有哪些？</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1）跨站脚本攻击（XSS）</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）分布式拒绝服务（DDOS）</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">基本方法是利用合理的请求占用服务器的大量资源，使正常用户无法得到服务器的相应。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）跨站请求伪造攻击（CSRF）</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">攻击者通过各种方法伪造一个请求，模仿用户提交表单的行为，从而达到修改用户的数据，或者执行特定任务的目的。解决方法是尽量使用post请求。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（4）sql注入</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">攻击主要针对后台使用sql拼接方式查询的情况，攻击者注入类似or 1 = 1的sql语句，致使表内的全部信息泄露。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">post请求和get请求的区别</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1） get请求一般用来获得数据，而post请求一般用来发送数据。人们期望，get请求不会对服务器造成任何影响，而post请求则可能会影响服务器端的数据。get请求消耗的资源较post请求而言，会少一些，但相对安全性较差。发送同样大小的数据，get请求的效率最高可以达到post请求的2倍。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）一般按照约定，使用get请求时，将数据通过url进行传递，而是用post请求时，将数据放在body里。但这并非硬性规定，因为method和data本身是正交的。post请求亦可将数据放在url中。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）就协议底层实现而言，在get请求中，只产生一个TCP数据包，浏览器会将header和data一并发送出去，等待服务器的回应；而在post请求中，会产生2个TCP数据包。，浏览器先发送header，服务器响应100 continue，浏览器再发送data。</p><h4 style="margin: 0px; padding: 0px; font-size: 14px; font-weight: 400; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">站内跳转和外部重定向有何区别</h4><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（1）直观而言，站内跳转不会改变url，而外部重定向会改变url。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（2）重定向可以跳转到web应用以外的页面，而站内跳转则不行。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">（3）站内跳转可以通过request传递数据，而重定向不可传递数据。</p><p><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">作者： 圣经的旋律&nbsp;</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">链接：http://www.imooc.com/article/16746</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">来源：慕课网</span></p><p><br/></p>', '前端面试题总结 HTTP\\浏览器篇 （一）', 'cookie和session的机制是什么？有什么区别？\r\n会话（Session）跟踪是Web程序中常用的技术，用来跟踪用户的整个会话，它可以弥补http协议无状态的不足。常用的会话跟踪技术是cookie与session。cookie通过在客户端记录信息确定用户身份，session通过在服务器端记录信息确定用户身份。\r\ncookie有什么作用？\r\ncookie可以解决http的无状态的问题，与服务器进行交互，作为http规范存在。它具有极高的简便性、可扩展性和可用性，也可以通过加密和SSL技术来提高其安全性。因此推荐使用cookie作为标识而不是身份验证的工具。', 1, 1, '2017-04-01 13:22:13', NULL, 0, '["29"]', '', 1, '', 0, '{"thumb":"","template":""}', 1, 0, 0, 1);
INSERT INTO `blog_posts` (`id`, `post_author`, `post_keywords`, `post_source`, `post_date`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `post_modified`, `post_content_filtered`, `post_parent`, `post_term`, `term_name`, `post_type`, `post_mime_type`, `comment_count`, `smeta`, `post_hits`, `post_like`, `istop`, `recommended`) VALUES
(5, 1, '', '慕课网http://www.imooc.com/article/17133', '2017-03-30 13:57:17', '<p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">this 的指向</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">this 是 js 中定义的关键字，它自动定义于每一个函数域内，但是它的指向却让人很迷惑。在实际应用中，this 的指向大致可以分为以下四种情况。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">1.作为普通函数调用</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">当函数作为一个普通函数被调用，this 指向全局对象。在浏览器里，全局对象就是 window。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">window.name&nbsp;=&nbsp;&#39;linxin&#39;;function&nbsp;getName(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(this.name);}getName();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">可以看出，此时 this 指向了全局对象 window。<br/>在ECMAScript5的严格模式下，这种情况 this 已经被规定不会指向全局对象了，而是 undefined。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">&#39;use&nbsp;strict&#39;;function&nbsp;fun(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(this);}fun();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;undefined</pre><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">2.作为对象的方法调用</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">当函数作为一个对象里的方法被调用，this 指向该对象</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name&nbsp;:&nbsp;&#39;linxin&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;getName&nbsp;:&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(this.name);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}}obj.getName();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">如果把对象的方法赋值给一个变量，再调用这个变量：</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;fun1&nbsp;:&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(this);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}}var&nbsp;fun2&nbsp;=&nbsp;obj.fun1;fun2();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;window</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">此时调用 fun2 方法 输出了 window 对象，说明此时 this 指向了全局对象。给 fun2 赋值，其实是相当于：</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;fun2&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(this);}</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">可以看出，此时的 this 已经跟 obj 没有任何关系了。这时 fun2 是作为普通函数调用。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">3.作为构造函数调用</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">js中没有类，但是可以从构造器中创建对象，并提供了 new 运算符来进行调用该构造器。构造器的外表跟普通函数一样，大部分的函数都可以当做构造器使用。当构造函数被调用时，this 指向了该构造函数实例化出来的对象。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;Person&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;this.name&nbsp;=&nbsp;&#39;linxin&#39;;}var&nbsp;obj&nbsp;=&nbsp;new&nbsp;Person();console.log(obj.name);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">如果构造函数显式的返回一个对象，那么 this 则会指向该对象。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;Person&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;this.name&nbsp;=&nbsp;&#39;linxin&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;name&nbsp;:&nbsp;&#39;linshuai&#39;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}}var&nbsp;obj&nbsp;=&nbsp;new&nbsp;Person();console.log(obj.name);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linshuai</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">如果该函数不用 new 调用，当作普通函数执行，那么 this 依然指向全局对象。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">4.call() 或 apply() 调用</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">通过调用函数的 call() 或 apply() 方法可动态的改变 this 的指向。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj1&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name&nbsp;:&nbsp;&#39;linxin&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;getName&nbsp;:&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(this.name);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}}var&nbsp;obj2&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name&nbsp;:&nbsp;&#39;linshuai&#39;}obj1.getName();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxinobj1.getName.call(obj2);&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linshuaiobj1.getName.apply(obj2);&nbsp;&nbsp;&nbsp;//&nbsp;linshuai</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这两个方法在js中都是非常常用的方法，可以阅读下一篇：<a href="https://github.com/lin-xin/blog/issues/7" target="_blank" rel="nofollow" style="outline: 0px; text-decoration: none; color: rgb(20, 25, 30);">javascript 中 apply 、call 的详解</a>。</p><p><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">作者： linshuai&nbsp;</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">链接：http://www.imooc.com/article/17133</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">来源：慕课网</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">本文原创发布于慕课网 ，转载请注明出处，谢谢合作！</span></p><p><br/></p>', 'JavaScript 中 this 的详解 ', 'this 是 js 中定义的关键字，它自动定义于每一个函数域内，但是它的指向却让人很迷惑。在实际应用中，this 的指向大致可以分为以下四种情况。\r\n1.作为普通函数调用\r\n当函数作为一个普通函数被调用，this 指向全局对象。在浏览器里，全局对象就是 window。\r\nwindow.name = ''linxin'';function getName(){\r\n    console.log(this.name);}getName();                   // linxin\r\n可以看出，此时 this 指向了全局对象 window。\r\n在ECMAScript5的严格模式下，这种情况 this 已经被规定不会指向全局对象了，而是 undefined。\r\n''use strict'';function fun(){\r\n    console.log(this);}fun();                      // undefined\r\n2.作为对象的方法调用\r\n当函数作为一个对象里的方法被调用，this 指向该对象', 1, 1, '2017-04-01 13:22:03', NULL, 0, '["16"]', '', 1, '', 0, '{"thumb":"","template":""}', 6, 0, 0, 0),
(6, 1, '', '慕课网http://www.imooc.com/article/17173', '2017-03-30 13:58:03', '<p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">闭包是什么</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在 JavaScript 中，闭包是一个让人很难弄懂的概念。ECMAScript 中给闭包的定义是：闭包，指的是词法表示包括不被计算的变量的函数，也就是说，函数可以使用函数之外定义的变量。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">是不是看完这个定义感觉更加懵逼了？别急，我们来分析一下。</p><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>闭包是一个函数</p></li><li><p>闭包可以使用在它外面定义的变量</p></li><li><p>闭包存在定义该变量的作用域中</p></li></ul><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">好像有点清晰了，但是使用在它外面定义的变量是什么意思，我们先来看看变量作用域。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">变量作用域</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">变量可分为全局变量和局部变量。全局变量的作用域就是全局性的，在 js 的任何地方都可以使用全局变量。在函数中使用 var 关键字声明变量，这时的变量即是局部变量，它的作用域只在声明该变量的函数内，在函数外面是访问不到该变量的。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;func&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;&#39;linxin&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(a);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin}func();console.log(a);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;Uncaught&nbsp;ReferenceError:&nbsp;a&nbsp;is&nbsp;not&nbsp;defined</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">作用域相对比较简单，我们不多讲，来看看跟闭包关系比较大的变量生存周期。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">变量生存周期</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">全局变量，生命周期是永久的。局部变量，当定义该变量的函数调用结束时，该变量就会被垃圾回收机制回收而销毁。再次调用该函数时又会重新定义了一个新变量。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;func&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;&#39;linxin&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(a);}func();</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">a 为局部变量，在 func 调用完之后，a 就会被销毁了。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;func&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;&#39;linxin&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;func1&nbsp;=&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a&nbsp;+=&nbsp;&#39;&nbsp;a&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(a);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;func1;}var&nbsp;func2&nbsp;=&nbsp;func();func2();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin&nbsp;afunc2();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin&nbsp;a&nbsp;afunc2();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin&nbsp;a&nbsp;a&nbsp;a</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">可以看出，在第一次调用完 func2 之后，func 中的变量 a 变成 &#39;linxin a&#39;，而没有被销毁。因为此时 func1 形成了一个闭包，导致了 a 的生命周期延续了。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这下子闭包就比较明朗了。</p><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>闭包是一个函数，比如上面的 func1 函数</p></li><li><p>闭包使用其他函数定义的变量，使其不被销毁。比如上面 func1 调用了变量 a</p></li><li><p>闭包存在定义该变量的作用域中，变量 a 存在 func 的作用域中，那么 func1 也必然存在这个作用域中。</p></li></ul><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">现在可以说，满足这三个条件的就是闭包了。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">下面我们通过一个简单而又经典的例子来进一步熟悉闭包。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;4;&nbsp;i++)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;setTimeout(function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(i)\r\n&nbsp;&nbsp;&nbsp;&nbsp;},&nbsp;0)}</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">我们可能会简单的以为控制台会打印出 0 1 2 3，可事实却打印出了 4 4 4 4，这又是为什么呢？我们发现，setTimeout 函数时异步的，等到函数执行时，for循环已经结束了，此时的 i 的值为 4，所以 function() { console.log(i) } 去找变量 i，只能拿到 4。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">我们想起上一个例子中，闭包使 a 变量的值被保存起来了，那么这里我们也可以用闭包把 0 1 2 3 保存起来。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;4;&nbsp;i++)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;(function&nbsp;(i)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;setTimeout(function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(i)\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},&nbsp;0)\r\n&nbsp;&nbsp;&nbsp;&nbsp;})(i)}</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">当 i=0 时，把 0 作为参数传进匿名函数中，此时 function(i){} 此匿名函数中的 i 的值为 0，等到 setTimeout 执行时顺着外层去找 i，这时就能拿到 0。如此循环，就能拿到想要的 0 1 2 3。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">内存管理</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在闭包中调用局部变量，会导致这个局部变量无法及时被销毁，相当于全局变量一样会一直占用着内存。如果需要回收这些变量占用的内存，可以手动将变量设置为null。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">然而在使用闭包的过程中，比较容易形成 JavaScript 对象和 DOM 对象的循环引用，就有可能造成内存泄露。这是因为浏览器的垃圾回收机制中，如果两个对象之间形成了循环引用，那么它们都无法被回收。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;test&nbsp;=&nbsp;document.getElementById(&#39;test&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;test.onclick&nbsp;=&nbsp;function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&#39;hello&nbsp;world&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在上面例子中，func 函数中用匿名函数创建了一个闭包。变量 test 是 JavaScript 对象，引用了 id 为 test 的 DOM 对象，DOM 对象的 onclick 属性又引用了闭包，而闭包又可以调用 test ，因而形成了循环引用，导致两个对象都无法被回收。要解决这个问题，只需要把循环引用中的变量设为 null 即可。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;test&nbsp;=&nbsp;document.getElementById(&#39;test&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;test.onclick&nbsp;=&nbsp;function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&#39;hello&nbsp;world&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;test&nbsp;=&nbsp;null;}</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">如果在 func 函数中不使用匿名函数创建闭包，而是通过引用一个外部函数，也不会出现循环引用的问题。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;test&nbsp;=&nbsp;document.getElementById(&#39;test&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;test.onclick&nbsp;=&nbsp;funcTest;}function&nbsp;funcTest(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(&#39;hello&nbsp;world&#39;);}</pre><p><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">作者： linshuai&nbsp;</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">链接：http://www.imooc.com/article/17173</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">来源：慕课网</span></p><p><br/></p>', 'JavaScript 中 闭包 的详解', '在 JavaScript 中，闭包是一个让人很难弄懂的概念。ECMAScript 中给闭包的定义是：闭包，指的是词法表示包括不被计算的变量的函数，也就是说，函数可以使用函数之外定义的变量。\r\n是不是看完这个定义感觉更加懵逼了？别急，我们来分析一下。\r\n闭包是一个函数\r\n闭包可以使用在它外面定义的变量\r\n闭包存在定义该变量的作用域中\r\n好像有点清晰了，但是使用在它外面定义的变量是什么意思，我们先来看看变量作用域。', 1, 1, '2017-04-01 13:21:54', NULL, 0, '["16"]', '', 1, '', 0, '{"thumb":"","template":""}', 4, 0, 0, 1),
(7, 1, '', '慕课网http://www.imooc.com/article/17157', '2017-03-30 13:59:53', '<p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">apply 和 call 的区别</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">ECMAScript 规范给所有函数都定义了 call 与 apply 两个方法，它们的应用非常广泛，它们的作用也是一模一样，只是传参的形式有区别而已。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">apply( )</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">apply 方法传入两个参数：一个是作为函数上下文的对象，另外一个是作为函数参数所组成的数组。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name&nbsp;:&nbsp;&#39;linxin&#39;}function&nbsp;func(firstName,&nbsp;lastName){\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(firstName&nbsp;+&nbsp;&#39;&nbsp;&#39;&nbsp;+&nbsp;this.name&nbsp;+&nbsp;&#39;&nbsp;&#39;&nbsp;+&nbsp;lastName);}func.apply(obj,&nbsp;[&#39;A&#39;,&nbsp;&#39;B&#39;]);&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;A&nbsp;linxin&nbsp;B</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">可以看到，obj 是作为函数上下文的对象，函数 func 中 this 指向了 obj 这个对象。参数 A 和 B 是放在数组中传入 func 函数，分别对应 func 参数的列表元素。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">call( )</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">call 方法第一个参数也是作为函数上下文的对象，但是后面传入的是一个参数列表，而不是单个数组。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name:&nbsp;&#39;linxin&#39;}function&nbsp;func(firstName,&nbsp;lastName)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(firstName&nbsp;+&nbsp;&#39;&nbsp;&#39;&nbsp;+&nbsp;this.name&nbsp;+&nbsp;&#39;&nbsp;&#39;&nbsp;+&nbsp;lastName);}func.call(obj,&nbsp;&#39;C&#39;,&nbsp;&#39;D&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;C&nbsp;linxin&nbsp;D</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">对比 apply 我们可以看到区别，C 和 D 是作为单独的参数传给 func 函数，而不是放到数组中。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">对于什么时候该用什么方法，其实不用纠结。如果你的参数本来就存在一个数组中，那自然就用 apply，如果参数比较散乱相互之间没什么关联，就用 call。</p><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">apply 和 call 的用法</span></p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">1.改变 this 指向</h3><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name:&nbsp;&#39;linxin&#39;}function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(this.name);}func.call(obj);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">我们知道，call 方法的第一个参数是作为函数上下文的对象，这里把 obj 作为参数传给了 func，此时函数里的 this 便指向了 obj 对象。此处 func 函数里其实相当于</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(obj.name);}</pre><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">2.借用别的对象的方法</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">先看例子</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;Person1&nbsp;&nbsp;=&nbsp;function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;this.name&nbsp;=&nbsp;&#39;linxin&#39;;}var&nbsp;Person2&nbsp;=&nbsp;function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;this.getname&nbsp;=&nbsp;function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(this.name);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;Person1.call(this);}var&nbsp;person&nbsp;=&nbsp;new&nbsp;Person2();person.getname();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">从上面我们看到，Person2 实例化出来的对象 person 通过 getname 方法拿到了 Person1 中的 name。因为在 Person2 中，Person1.call(this) 的作用就是使用 Person1 对象代替 this 对象，那么 Person2 就有了 Person1 中的所有属性和方法了，相当于 Person2 继承了 Person1 的属性和方法。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">3.调用函数</h3><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">apply、call 方法都会使函数立即执行，因此它们也可以用来调用函数。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(&#39;linxin&#39;);}func.call();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; background-color: rgb(255, 255, 255); display: block; font-size: 22px; margin: 22px 0px 10px;">call 和 bind 的区别</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在 EcmaScript5 中扩展了叫 bind 的方法，在低版本的 IE 中不兼容。它和 call 很相似，接受的参数有两部分，第一个参数是是作为函数上下文的对象，第二部分参数是个列表，可以接受多个参数。<br/>它们之间的区别有以下两点。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">1.bind 发返回值是函数</h3><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">var&nbsp;obj&nbsp;=&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;name:&nbsp;&#39;linxin&#39;}function&nbsp;func()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(this.name);}var&nbsp;func1&nbsp;=&nbsp;func.bind(obj);func1();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">bind 方法不会立即执行，而是返回一个改变了上下文 this 后的函数。而原函数 func 中的 this 并没有被改变，依旧指向全局对象 window。</p><h3 style="margin: 0px; padding: 0px; font-size: 16px; font-weight: 400; line-height: 32px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">2.参数的使用</h3><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">function&nbsp;func(a,&nbsp;b,&nbsp;c)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;console.log(a,&nbsp;b,&nbsp;c);}var&nbsp;func1&nbsp;=&nbsp;func.bind(null,&#39;linxin&#39;);func(&#39;A&#39;,&nbsp;&#39;B&#39;,&nbsp;&#39;C&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;A&nbsp;B&nbsp;Cfunc1(&#39;A&#39;,&nbsp;&#39;B&#39;,&nbsp;&#39;C&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin&nbsp;A&nbsp;Bfunc1(&#39;B&#39;,&nbsp;&#39;C&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin&nbsp;B&nbsp;Cfunc.call(null,&nbsp;&#39;linxin&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;linxin&nbsp;undefined&nbsp;undefined</pre><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">call 是把第二个及以后的参数作为 func 方法的实参传进去，而 func1 方法的实参实则是在 bind 中参数的基础上再往后排。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在低版本浏览器没有 bind 方法，我们也可以自己实现一个。</p><pre class="prettyprint" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(20, 25, 30); font-size: 14px; background-color: rgb(255, 255, 255);">if&nbsp;(!Function.prototype.bind)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;Function.prototype.bind&nbsp;=&nbsp;function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;self&nbsp;=&nbsp;this,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;保存原函数\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;context&nbsp;=&nbsp;[].shift.call(arguments),&nbsp;//&nbsp;保存需要绑定的this上下文\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;args&nbsp;=&nbsp;[].slice.call(arguments);&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;剩余的参数转为数组\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function&nbsp;()&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;返回一个新函数\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;self.apply(context,[].concat.call(args,&nbsp;[].slice.call(arguments)));\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><p><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">作者： linshuai&nbsp;</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">链接：http://www.imooc.com/article/17157</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">来源：慕课网</span><br style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(20, 25, 30); font-family: &quot;PingFang SC&quot;, 微软雅黑, &quot;Microsoft YaHei&quot;, Helvetica, &quot;Helvetica Neue&quot;, Tahoma, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);">本文原创发布于慕课网 ，转载请注明出处，谢谢合作！</span></p><p><br/></p>', 'JavaScript 中 apply 、call 的详解', 'apply 和 call 的区别\r\n\r\nECMAScript 规范给所有函数都定义了 call 与 apply 两个方法，它们的应用非常广泛，它们的作用也是一模一样，只是传参的形式有区别而已。\r\napply( )\r\napply 方法传入两个参数：一个是作为函数上下文的对象，另外一个是作为函数参数所组成的数组。\r\nvar obj = {\r\n    name : ''linxin''}function func(firstName, lastName){\r\n    console.log(firstName + '' '' + this.name + '' '' + lastName);}func.apply(obj, [''A'', ''B'']);    // A linxin B\r\n可以看到，obj 是作为函数上下文的对象，函数 func 中 this 指向了 obj 这个对象。参数 A 和 B 是放在数组中传入 func 函数，分别对应 func 参数的列表元素。\r\ncall( )\r\ncall 方法第一个参数也是作为函数上下文的对象，但是后面传入的是一个参数列表，而不是单个数组。', 1, 1, '2017-04-01 13:21:36', NULL, 0, '["16"]', '', 1, '', 0, '{"thumb":"","template":""}', 2, 0, 0, 1);
INSERT INTO `blog_posts` (`id`, `post_author`, `post_keywords`, `post_source`, `post_date`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `post_modified`, `post_content_filtered`, `post_parent`, `post_term`, `term_name`, `post_type`, `post_mime_type`, `comment_count`, `smeta`, `post_hits`, `post_like`, `istop`, `recommended`) VALUES
(8, 1, '', '', '2017-03-30 14:06:39', '<p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">结构标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">article：标记定义一篇文章</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">header：标记定义一个页面后者一个区域的头部</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">nav：标记定义导航链接</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">section：标记定义一个区域</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">aside：标记定义页面内容部分的侧边栏</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">hgroup：标记定义文件中一个区块的相关信息</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">figure：标记定义一组媒体内容以及他们的标题</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">figcaption：标签定义figure元素的标题</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">footer：标记定义一个页面或一个区域的底部</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">dialog：标记定义一个对话框（会话框）类似微信</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">第一点补充：header/section/aside/article/footer</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">第二点补充：header/section/foooter &gt;aside/article/figure/htroup/nav</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">多媒体标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">video：标记定义一个视频</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">audio：标记定义音频内容</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">source：标记定义媒体资源</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">canvas：画布很强大的API</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">embed：标记定义外部的可交互的内容或插件</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">多媒体标签意义：多媒体标签的出现亦为之富媒体的发展以及支持不适用插件的抢矿下即可操作媒体文件，极大的提升了用户体验</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">Web应用标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">状态标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">meter：状态标签（实时状体显示：气压气温）（Chrome Opera）</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">progress：状态标签（任务过程啊：安装 加载） (Chrome Firefox Opera)</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">列表标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">datalist：为input标记定义一个下拉列表，配合option（Firefox Opera）</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">details：标记定义一个元素的详细内容，配合summary( Chrome)</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">Menu标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">menu：命令列表（目前所有主流浏览器都不支持）</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">menuitem：menu命令里列表（只有FireFox8.0）支持</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">command：menu 标记定义一个命令按钮（只有IE9支持）</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">注释标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">ruby：标记定义注释或音标</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">rt：标记定义对ruby真的注释内容文本</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">rp：告诉那些不支持ruby元素的浏览器如何去显示</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">rp：不要放在rt标签内</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">其他标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">mark ：标记定义有标记的文本</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">output ：标记定义一些输入类型，计算表单结果配合oninput时间</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">keygen：标记定义表单里一个生成的建值</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">time：标记定义一个日期/时间，目前所有主流浏览器都不支持</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">删除的标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">纯表现的元素</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">Basefont big center font s strike tt u</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">对可用性产生负面影响的元素</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">frane frameset noframes</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">产生混淆的元素</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">acribym applet isindex dir</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">重定义标签</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">b：代表内联文本 通常是粗体 没有传递表示重要的意思</p><p style="margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(20, 25, 30);font-family: &#39;PingFang SC&#39;, 微软雅黑, &#39;Microsoft YaHei&#39;, Helvetica, &#39;Helvetica Neue&#39;, Tahoma, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">i：代表内联文本 通常是斜体 没有表示传递表示重要的意思</p><p style="marg